observer:listen.action:script:


  	videoPlayer.clearCuePoints();

	//NOTE: calling length() on XML calls length (the node) instead
	for(var i = 0; i < ObjectUtils.getLength(currentVideo.cue); i++)
	{
		var cue = currentVideo.cue[i];   
		var func:String = cue["@type"];  

		var funcInst = cueHandlers[func];

	   	if(funcInst)
		{                                                        
			videoPlayer.addCuePoint(funcInst,cue["@time"],(cue["@reusable"] == "true"),cue);
		}


	}
