Plugin.action:script:


    function mouseIsOut(target) 
	{
		return (root.mouseX < target.x) ||
		       (root.mouseY < target.y) ||
		       (root.mouseX > target.x + target.width) ||
		       (root.mouseY > target.y + target.height)
	} 


	var mouseIsIn:Boolean = true;

	stage.addEventListener("mouseLeave",onMouseLeave);

	function onMouseLeave(event)
	{
		mouseIsIn = false;

		Sleep.timeout(1000,hideControls);

		stage.addEventListener("mouseMove",onMouseEnter);
	}  

	function hideControls()
	{

		if(!mouseIsIn)
		{
			var tween:Tween = new Tween(mediaControls,'alpha',1,0,300);
			tween.easingFunction = hideControlsEffect; 
		}

	}

	function onMouseEnter(event)
	{
		mouseIsIn = true;

		stage.removeEventListener("mouseMove",onMouseEnter);

		var tween:Tween = new Tween(mediaControls,'alpha',0,1,300);


	}
