Plugin.action:script:


    var movieXMLData:String = flashVars.movieData;  

	var currentMovie:int = 0;
	var movieData:XML;

   	var loader:URLLoader = new URLLoader();
	loader.addEventListener(Event.COMPLETE,onVideoData);
	loader.load(new URLRequest(movieXMLData));

    function onVideoData(event:Event)
	{                      
		movieData = new XML(event.target.data);
		playList  = movieData.video;


		loadMovie(currentMovie);
	}


	function loadMovie(index:int)
	{   



		videoPlayer.stop();

		if(!(playList[index]) || (index == 0-1))
		{
			return false;
		}
		else
		{
			currentMovie = index;       

		}                 


		setMovie(playList[index]);

	}

	function setMovie(video)
	{       
		currentVideo = video;   

		videoPlayer.stop();   


	}
